import { Component, OnInit } from '@angular/core';
import { LeaderboardInfo } from '../types';
import route

@Component({
  selector: 'app-edit-leaderboard-page',
  templateUrl: './edit-leaderboard-page.component.html',
  styleUrls: ['./edit-leaderboard-page.component.css']
})
export class EditLeaderboardPageComponent implements OnInit{
  leaderboard!: LeaderboardInfo;

  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private leaderboardService: LeaderboardService,
  ) {}

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    this.leaderboardService.getPlayerInfo(id)
      .subscribe(player => this.player = player);
  }

  onSubmit({ firstName, lastName, gamertag, platform, age, regionID }: Player) : void {
    this.leaderboardService.editPlayer(this.player.playerID, firstName, lastName, gamertag, platform, age, regionID)
      .subscribe(() => {
        this.router.navigateByUrl('/query');
      }); 
  }
}
