import { Component, OnInit } from '@angular/core';
import { LeaderboardInfo } from '../types';
import { Router, ActivatedRoute } from '@angular/router';
import { LeaderboardService } from '../leaderboard.service';

@Component({
  selector: 'app-edit-leaderboard-page',
  templateUrl: './edit-leaderboard-page.component.html',
  styleUrls: ['./edit-leaderboard-page.component.css']
})
export class EditLeaderboardPageComponent implements OnInit{
  leaderboard!: LeaderboardInfo;

  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private leaderboardService: LeaderboardService,
  ) {}

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    this.leaderboardService.getLeaderboardInfo(id, 1)
      .subscribe(player => this.player = player);
  }

  onSubmit({ gameID, timePlayed, killDeathRatio, winLossRatio, playerRank }: LeaderboardInfo): void {
    const id = this.route.snapshot.paramMap.get('id');
    const pID = Number(id);
    this.leaderboardService.editLeaderboard(pID, gameID, timePlayed, killDeathRatio, winLossRatio, playerRank)
      .subscribe(() => {
        this.router.navigateByUrl('/query');
      }); 
  }
}
<div class="content-box">
    <h2>Edit Leaderboard Info</h2>
    <app-leaderboard-data-page 
        buttonText="Save Changes"
        [currentGameID]="leaderboard.gameID"
        [currentTimePlayed]="leaderboard.timePlayed"
        [currentKDR]="leaderboard.killDeathRatio"
        [currentWLR]="leaderboard.winLossRatio"
        [currentRank]="leaderboard.playerRank"
        (onSubmit)="onSubmit($event)"></app-leaderboard-data-page>
</div>
