import { Component, OnInit } from '@angular/core';
import { TopPerGame } from '../types';
import { LeaderboardService } from '../leaderboard.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-q3',
  templateUrl: './q3.component.html',
  styleUrls: ['./q3.component.css']
})
export class Q3Component implements OnInit {
  TopPerGames: TopPerGame[] = [];

  constructor(
    private leaderboardService: LeaderboardService,
    private router: Router,
  ) { }

  ngOnInit(): void {
    this.leaderboardService.getTopPerGame(1)
      .subscribe(TopPerGames => this.TopPerGames = TopPerGames);
  }
}
<div>
    <div>
        <h3>Top Players of the given game</h3>
    </div>
    <div class="content-box">
        <a routerLink="/q3/1">
            <button>Display Overwatch 2 query</button>
        </a>
        <a routerLink="/q3/2">
            <button>Display Valorant query</button>
        </a>
        <a routerLink="/q3/3">
            <button>Display League of Legends query</button>
        </a>
    </div>
</div>
<div class="content-box">
    <a routerLink="/query">
        <button>Back</button>
    </a>
</div>
