import { Component } from '@angular/core';
import { LeaderboardInfo } from '../types';
import { LeaderboardService } from '../leaderboard.service';
import { ActivatedRoute } from '@angular/router';
import { Router } from '@angular/router';

@Component({
  selector: 'app-leaderboard-info-page',
  templateUrl: './leaderboard-info-page.component.html',
  styleUrls: ['./leaderboard-info-page.component.css']
})
export class LeaderboardInfoPageComponent {
  leaderboard!: LeaderboardInfo;

  constructor(
    private leaderboardService: LeaderboardService,
    private route: ActivatedRoute,
    private router: Router,
  ) { }

  ngOnInit(): void {
    const pID = this.route.snapshot.paramMap.get('pID');
    const gID = this.route.snapshot.paramMap.get('gID');
    this.leaderboardService.getLeaderboardInfo(pID, gID)
      .subscribe(leaderboard => {
        this.leaderboard = leaderboard;
      });
  }

  onDeleteClicked(playerID: number): void {
    this.leaderboardService.deleteLeaderboard(this.leaderboard.playerID, this.leaderboard.gameID)
      .subscribe(() => {
        this.router.navigateByUrl('/q5');
      });
  }
}
