import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { BestGame, BestWinLoss, GamesByStudio, Player, TopPerGame, TopPlayerByGame, PlayerR, LeaderboardInfo } from './types';

const httpOptions = {
  headers: new HttpHeaders({
    'Content-Type': 'application/json'
  })
};

@Injectable({
  providedIn: 'root'
})
export class LeaderboardService {

  constructor(
    private http: HttpClient,
  ) { }

  getPlayers(): Observable<Player[]> {
    return this.http.get<Player[]>('/api/listings');
  }

  getPlayerInfo(id: string | null): Observable<PlayerR> {
    return this.http.get<PlayerR>(`/api/listings/${id}`);
  }

  getTopPerGame(gameID: number): Observable<TopPerGame[]> {
    return this.http.get<TopPerGame[]>(`/api/q3/${gameID}`);
  }

  getGamesByStudio(): Observable<GamesByStudio[]> {
    return this.http.get<GamesByStudio[]>('/api/q4');
  }

  getTopPlayer(): Observable<TopPlayerByGame[]> {
    return this.http.get<TopPlayerByGame[]>('/api/q5');
  }

  getWinLoss(): Observable<BestWinLoss[]> {
    return this.http.get<BestWinLoss[]>('/api/q7');
  }

  getBestGame(): Observable<BestGame[]> {
    return this.http.get<BestGame[]>('/api/q8');
  }

  createPlayer(firstName: string, lastName: string, gamertag: string, platform: string, age: number, regionID: number): Observable<Player> {
    return this.http.post<Player>(
      '/api/listings',
      { firstName, lastName, gamertag, platform, age, regionID },
      httpOptions,
    );
  }

  createLeaderboard(gameID: number, timePlayed: number, killDeathRatio: number, winLossRatio: number, playerRank: string): Observable<LeaderboardInfo> {
    return this.http.post<LeaderboardInfo>(
      `/api/leaderboard/${id}`,
      { gameID, timePlayed, killDeathRatio, winLossRatio, playerRank },
      httpOptions,
    );
  }

  editPlayer(id: number, firstName: string, lastName: string, gamertag: string, platform: string, age: number, regionID: number): Observable<Player> {
    return this.http.post<Player>(
      `/api/listings/${id}`,
      { firstName, lastName, gamertag, platform, age, regionID },
    );
  }

  deletePlayer(id: number): Observable<any> {
    return this.http.delete(`/api/listings/${id}`);
  }
}
