import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Player } from './types';

const httpOptions = {
  headers: new HttpHeaders({
    'Content-Type': 'application/json'
  })
};

@Injectable({
  providedIn: 'root'
})
export class LeaderboardService {

  constructor(
    private http: HttpClient,
  ) { }

  getPlayers(): Observable<Player[]> {
    return this.http.get<Player[]>('/api/listings');
  }

  getPlayerInfo(id: string | null): Observable<Player> {
    return this.http.get<Player>(`/api/listings/${id}`)
  }

  createPlayer(firstName: string, lastName: string, gamertag: string, platform: string, age: number, regionID: number): Observable<Player> {
    return this.http.post<Player>(
      '/api/listings',
      { firstName, lastName, gamertag, platform, age, regionID },
      httpOptions,
    );
  }

  editPlayer(firstName: string, lastName: string, gamertag: string, platform: string, age: number, regionID: number): Observable<Player> {
    return this.http.post<Player>(
      `/api/listings/${id}`,
      {}
    )
  }

  deletePlayer(playerID: number): Observable<any> {
    return new Observable<any>(observer => {
      this.http.delete(`/api/listings/${playerID}`, httpOptions)
        .subscribe(() => observer.next());
    })
  }
}
