import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { LeaderboardService } from '../leaderboard.service';

@Component({
  selector: 'app-add-object',
  templateUrl: './add-object.component.html',
  styleUrls: ['./add-object.component.css']
})
export class AddObjectComponent implements OnInit {
  // change to person stats
  firstName: string = '';
  lastName: string = '';
  gamertag: string = '';
  platform: string = '';
  age: number = 0;
  regionID: number = 0;

  constructor(
    private router: Router,
    private leaderboardService: LeaderboardService,
  ) { }

  ngOnInit(): void {
  }

  onButtonClicked(): void {
    this.onSubmit.emit({
      firstName: this.firstName,
      lastName: this.lastName,
      gamer
    })
  }

  onSubmit({ firstName, lastName, gamertag, platform, age, regionID}): void {
    this.leaderboardService.createPlayer(firstName, lastName, gamertag, platform, age, regionID)
      .subscribe(() => {
        this.router.navigateByUrl('/query');
      }); 
  }
}
<div class="content-box">
    <h2>Create New Listing</h2>
    <form (ngSubmit)="onSubmit()">
        <div>
            <label for="firstName">
                First Name:
            </label>
            <input id="firstName" name="firstName" type="text" (ngModel)="firstName" />
        </div>
        <div>
            <label for="lastName">
                Last Name:
            </label>
            <input id="lastName" name="lastName" type="text" (ngModel)="lastName" />
        </div>
        <div>
            <label for="gamertag">
                Gamertag:
            </label>
            <input id="gamertag" name="gamertag" type="text" (ngModel)="gamertag" />
        </div>
        <div>
            <label for="platform">
                Platform:
            </label>
            <input id="platform" name="platform" type="text" (ngModel)="platform" />
        </div>
        <div>
            <label for="age">
                Age:
            </label>
            <input id="age" name="age" type="number" (ngModel)="age" />
        </div>
        <div>
            <label for="region">
                Region:
            </label>
            <!-- <input id="region" name="region" type="text" (ngModel)="region" /> -->
            <select id="regionID" name="regionID" (ngModel)="regionID">
                <option value="1">North America</option>
                <option value="2">Europe</option>
                <option value="3">Asia</option>
                <option value="4">South America</option>
                <option value="5">Australia</option>
                <option value="6">Africa</option>
            </select>
        </div>
        <button (click)="onSubmit($event)">Create Listing</button>
    </form>
</div>
