import { Component, OnInit } from '@angular/core';
import { LeaderboardInfo } from '../types';
import { Router, ActivatedRoute } from '@angular/router';
import { LeaderboardService } from '../leaderboard.service';

@Component({
  selector: 'app-edit-leaderboard-page',
  templateUrl: './edit-leaderboard-page.component.html',
  styleUrls: ['./edit-leaderboard-page.component.css']
})
export class EditLeaderboardPageComponent implements OnInit{
  leaderboard!: LeaderboardInfo;

  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private leaderboardService: LeaderboardService,
  ) {}

  ngOnInit(): void {
  }

  onSubmit({ firstName, lastName, gamertag, platform, age, regionID }: Player) : void {
    this.leaderboardService.editPlayer(this.player.playerID, firstName, lastName, gamertag, platform, age, regionID)
      .subscribe(() => {
        this.router.navigateByUrl('/query');
      }); 
  }
}
<div class="content-box">
    <h2>Edit Leaderboard Info</h2>
    <app-leaderboard-data-page 
        buttonText="Save Changes"
        [currentFName]="player.firstName"
        [currentLName]="player.lastName"
        [currentGamertag]="player.gamertag"
        [currentPlatform]="player.platform"
        [currentAge]="player.age"
        [currentRegion]="player.regionID"
        (onSubmit)="onSubmit($event)"></app-leaderboard-data-page>
</div>
